<style lang="scss">
  .skills {
    padding: 120px;
    background-color: var(--background);

    h2 {
      color: var(--high-contrast-text);
      text-align: center;
      font-size: 3rem;
      font-weight: bold;
      margin-bottom: 3rem;
    }

    .card-section {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 2rem;
      justify-items: center;
      transition: filter 0.3s ease-in-out;

      &:has(.card:hover) .card:not(:hover) {
        filter: blur(5px);
      }

      .card {
        background: linear-gradient(
          135deg,
          var(--interactive) 0%,
          var(--ring) 100%
        );
        width: 320px;
        height: 380px;
        border-radius: 25px;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: center;
        text-align: center;
        padding: 2rem;
        border: none;
        position: relative;
        box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
        transition:
          transform 0.3s ease,
          box-shadow 0.3s ease,
          background 0.3s ease;

        &::before {
          content: "";
          position: absolute;
          inset: 0;
          border-radius: inherit;
          background: rgba(255, 255, 255, 0.05);
          backdrop-filter: blur(10px);
          z-index: 0;
        }

        * {
          position: relative;
          z-index: 1;
        }

        &:hover {
          transform: translateY(-12px);
        }

        h3 {
          font-size: 2.2rem;
          font-weight: bold;
          color: var(--high-contrast-text);
        }

        p {
          font-size: 1.2rem;
          color: var(--secondary-text);
        }

        .cardImg {
          width: 90px;
          height: 90px;
          border-radius: 50%;
          background: rgba(255, 255, 255, 0.1);
          padding: 10px;
        }
      }

      .front-end,
      .back-end,
      .tools {
        & > svg g {
          fill: var(--secondary-text);
        }

        ul {
          display: flex;
          justify-content: center;
          align-items: center;
          gap: 15px;

          svg {
            height: 30px;
            width: 35px;
            cursor: pointer;
            transition: transform 0.3s ease;

            path,
            rect,
            polygon,
            polyline {
              transition: fill 0.3s ease;
            }
          }
        }
      }
    }

    .front-end {
      .html {
        &::before {
          content: "HTML";
        }

        svg:hover {
          transform: translateY(-2px);
          path:nth-child(1) {
            fill: #e44d26;
          }
          path:nth-child(2) {
            fill: #f16529;
          }
        }
      }

      .css {
        &::before {
          content: "CSS";
        }

        svg:hover {
          transform: translateY(-2px);
          path:nth-child(1) {
            fill: #1172b8;
          }
          path:nth-child(2) {
            fill: #33aadd;
          }
        }
      }

      .scss {
        &::before {
          content: "scss";
        }
        svg:hover {
          transform: translateY(-2px);
          path:nth-child(2) {
            fill: #c69 !important;
          }
        }
      }

      .javascript {
        &::before {
          content: "JavaScript";
        }
        svg:hover {
          transform: translateY(-2px);
          rect {
            fill: #ffca28;
          }
          path:nth-child(3),
          path:nth-child(2) {
            fill: #3e3e3e;
          }
        }
      }
      .angular {
        &::before {
          content: "Angular";
        }
        svg:hover {
          transform: translateY(-2px);
          path:nth-child(1) {
            fill: #dd0031;
          }
          path:nth-child(2) {
            fill: #c3002f;
          }
        }
      }
    }

    .back-end {
      ul {
        .php {
          &::before {
            content: "PHP";
          }
          svg:hover {
            transform: translateY(-2px);

            ellipse {
              fill: #8993be;
            }
          }
        }
        .laravel {
          &::before {
            content: "Laravel";
          }
          svg:hover {
            transform: translateY(-2px);

            path {
              fill: #ff2d20;
            }
          }
        }
      }
    }

    .tools {
      ul {
        svg {
          position: relative;
          left: -2px;
        }

        .git {
          &::before {
            content: "Git";
          }
          svg:hover {
            transform: translateY(-2px);

            path:nth-child(1) {
              fill: #ee513b;
            }
          }
        }
      }
    }

    ul {
      flex-wrap: wrap;

      .html,
      .css,
      .scss,
      .javascript,
      .angular,
      .php,
      .laravel,
      .git {
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        background-color: #f1f1f1;
        padding: 10px;
        border-radius: 10px;
        transition: background 0.3s ease-in-out;

        &:hover {
          background: var(--high-contrast-text);
        }

        &::before {
          opacity: 0;
          visibility: hidden;
          width: max-content;
          padding: 8px 12px;
          background: rgba(0, 0, 0, 0.85);
          color: white;
          font-size: 14px;
          font-weight: 500;
          border-radius: 8px;
          position: absolute;
          top: 0;
          left: 50%;
          transform: translateX(-50%);
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
          transition:
            top 0.3s ease-in-out,
            opacity 0.3s ease-in-out,
            visibility 0s linear 0.3s;
        }

        &:hover::before {
          opacity: 1;
          visibility: visible;
          top: -40px;
          transition-delay: 0s;
        }
      }
    }
  }

  @media (max-width: 768px) {
    .skills {
      padding: 80px 20px;
    }

    .card-section {
      grid-template-columns: 1fr;
    }
  }
</style>
